DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `category_id` int(10) NOT NULL,
  `title` varchar(50) NOT NULL,
  `keyword` varchar(200) DEFAULT NULL,
  `content` text,
  `writer` varchar(50) DEFAULT NULL,
